                         SEGusuaru_2.NOMUSUARI AS [USUARIO CREA], 
                         CASE WHEN ADINGRESO.UFUCODIGO = '01' THEN 'URGENCIAS CONSULTA Y PROCEDIMIENTOS' WHEN ADINGRESO.UFUCODIGO = '031' THEN 'URGENCIAS OBSERVACION PEDIATRICA'
