                         INDIGO031.dbo.INPACIENT ON ADINGRESO.IPCODPACI = INPACIENT.IPCODPACI INNER JOIN
                         INDIGO031.dbo.INENTIDAD ON ADINGRESO.CODENTIDA = INENTIDAD.CODENTIDA LEFT OUTER JOIN
                         INDIGO031.dbo.SEGusuaru ON ADINGRESO.CODUSUANU = SEGusuaru.CODUSUARI LEFT OUTER JOIN
                         INDIGO031.dbo.SEGusuaru AS SEGusuaru_2 ON ADINGRESO.CODUSUCRE = SEGusuaru_2.CODUSUARI LEFT OUTER JOIN
                         INDIGO031.dbo.SEGusuaru AS SEGusuaru_1 ON ADINGRESO.CODUSUMOD = SEGusuaru_1.CODUSUARI LEFT OUTER JOIN
						 INDIGO031.Billing.SlipOut AS BS ON ADINGRESO.NUMINGRES =BS.AdmissionNumber
